import useSWR, { mutate } from "swr";
import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import React, { useState } from "react";
import { Form } from "react-bootstrap";
import Link from "next/link";
import gql from "graphql-tag";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { format, intervalToDuration, parseISO } from "date-fns";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";
import "@fullcalendar/list/main.css";
import { TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import interactionPlugin from "@fullcalendar/interaction";
import timeGridPlugin from "@fullcalendar/timegrid";
import listPlugin from "@fullcalendar/list";
import { hasuraUserClient } from "../../lib/hasura-user-client";
const today = new Date().toISOString().substring(0, 19);
const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    flexWrap: "wrap",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));
const GetEvents = gql`
  query MyQuery {
    Event {
      id
      Title
      description
      start_date
    }
  }
`;
const AddNotification = gql`
  mutation MyMutation($user_id: Int!) {
    insert_notification_one(
      object: {
        user_id: $user_id
        name: "test"
        seen: false
        description: "test"
      }
    ) {
      user_id
      seen
      name
      id
      description
    }
  }
`;
const insertEvent = gql`
  mutation MyMutation(
    $Title: String!
    $description: String!
    $start_date: timestamp!
  ) {
    insert_Event_one(
      object: {
        Title: $Title
        description: $description
        start_date: $start_date
      }
    ) {
      Title
      description
      start_date
    }
  }
`;
const getNotifications = gql`
  query MyQuery {
    notification(where: { user_id: { _is_null: true } }) {
      description
      name
      seen
    }
    notification_aggregate(where: { user_id: { _is_null: true } }) {
      aggregate {
        count
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();
  const initialData = await hasura.request(GetEvents);
  const datar = await hasura.request(getNotifications);

  return {
    props: {
      initialData,
      datar,
    },
    revalidate: 1,
  };
};

export default function Projects({ initialData, datar }) {
  const { isAuthenticated, user } = useAuthState();

  const classes = useStyles();
  console.log(today);
  console.log(datar);
  const [startDate, setStartDate] = useState(new Date());
  const [EndDate, setEndDate] = useState(new Date());
  const [File, setFile] = useState(null);
  const [createObjectURL, setCreateObjectURL] = useState(null);
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const hasura = hasuraUserClient();
  const router = useRouter();
  const { data } = useSWR(GetEvents, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const onSubmit = async ({ Title, datetime, description }) => {
    console.log(datetime);
    const start_date = datetime;
    try {
      const { insert_Event_one } = await hasura.request(insertEvent, {
        Title,
        description,
        start_date,
      });
      if (isAuthenticated) {
        const { insert_notification_one } = await hasura.request(
          AddNotification,
          {
            user_id: user.id,
          }
        );
      }

      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };
  const handleDateClick = (arg) => {
    // bind with an arrow function
    alert(arg.dateStr);
    // alert("Coordinates: " + arg.jsEvent.pageX + "," + arg.jsEvent.pageY);
    // alert("Current view: " + arg.view.type);
    // change the day's background color just for fun
    arg.dayEl.style.backgroundColor = "red";
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        {/* <h1 class="page-title"> Calendar</h1> */}
        <div class="row">
          <div class="col-md-6">
            <div class="portlet light portlet-fit bordered calendar">
              <div class="portlet-title">
                <div class="caption">
                  <i class=" icon-layers font-green"></i>
                  <span class="caption-subject font-green sbold uppercase">
                    Feed
                  </span>
                </div>
              </div>
              <div class="portlet-body">
                <div class="row">
                  <h3 class="event-form-title margin-bottom-20">Event</h3>
                  <div class="portlet-body">
                    <div class="tab-content">
                      <div class="tab-pane active" id="tab_1_1">
                        <div
                          class="scroller"
                          style={{ height: 320 }}
                          data-always-visible="1"
                          data-rail-visible1="0"
                          data-handle-color="#D7DCE2"
                        >
                          <ul class="feeds">
                            {" "}
                            {datar.notification.map(({ id, Title }) => {
                              return (
                                <a
                                  href="javascript:;"
                                  class="list-group-item list-group-item-success"
                                >
                                  {" "}
                                  Success{" "}
                                </a>
                              );
                            })}
                          </ul>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>{" "}
          <div class="col-md-6">
            {" "}
            <div class="portlet light portlet-fit bordered calendar">
              <div class="portlet-title">
                <div class="caption">tttttt</div>
              </div>
            </div>
          </div>
        </div>

        <div class="row">
          <div class="col-md-12">
            <div class="portlet light portlet-fit bordered calendar">
              <div class="portlet-title">
                <div class="caption">
                  <i class=" icon-layers font-green"></i>
                  <span class="caption-subject font-green sbold uppercase">
                    Calendar
                  </span>
                </div>
              </div>
              <div class="portlet-body">
                <div class="row">
                  <div class="col-md-3 col-sm-12">
                    <h3 class="event-form-title margin-bottom-20">Event</h3>
                    <div id="external-events">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {" "}
                        <div class="form-group">
                          <input
                            type="text"
                            name="Title"
                            class="form-control"
                            placeholder="Event Type..."
                            id="event_title"
                            ref={register}
                          />{" "}
                        </div>{" "}
                        <div class="form-group">
                          <input
                            type="text"
                            name="description"
                            class="form-control"
                            placeholder="Description"
                            id="event_title"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label>Next appointment</label>
                          <TextField
                            id="datetime"
                            name="datetime"
                            type="datetime-local"
                            defaultValue={today}
                            inputRef={register}
                            className={classes.textField}
                          />
                        </div>
                        <div class="form-group">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Add Event{" "}
                          </button>
                        </div>
                      </form>
                      <hr />
                      <hr class="visible-xs" />{" "}
                    </div>
                  </div>
                  <div class="col-md-9 col-sm-12">
                    <FullCalendar
                      plugins={[
                        dayGridPlugin,
                        timeGridPlugin,
                        interactionPlugin,
                        listPlugin,
                      ]}
                      headerToolbar={{
                        left: "prev,next today",
                        center: "title",
                        right: "dayGridMonth,timeGridWeek,timeGridDay,listWeek",
                      }}
                      initialView="dayGridMonth"
                      editable={true}
                      selectable={true}
                      selectMirror={true}
                      dayMaxEvents={true}
                      eventRemove
                      events={data.Event.map(({ id, Title, start_date }) => {
                        return { title: Title, date: start_date };
                      })}
                      dateClick={handleDateClick}
                      eventColor="#378006"

                      // weekends={this.state.weekendsVisible}
                      // initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
                      // select={this.handleDateSelect}
                      // eventContent={renderEventContent} // custom render function
                      // eventsSet={this.handleEvents} // called after events are initialized/added/changed/removed
                      /* you can update a remote database when these fire:
                eventAdd={function(){}}
                eventChange={function(){}}
                */
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
