import useSWR, { mutate } from "swr";
import formatRelative from "date-fns/formatRelative";
import differenceInMinutes from "date-fns/differenceInMinutes";
import Link from "next/link";
import Layout from "../Components/Layout";
import gql from "graphql-tag";
import { useRouter } from "next/router";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import TodoFilters from "../Components/Todo/TodoFilters";
import $ from "jquery";

const Todos = gql`
  query MyQuery {
    User {
      id
    }
  }
`;
const GetCandidats = gql`
  query MyQuery($id: Int!) {
    Todo(where: { user_id: { _eq: $id } }) {
      Name
      user_id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(Todos);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};
export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  console.log(params);

  const initialData = await hasura.request(GetCandidats, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function TodoList({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const { id } = query;
  const { data } = useSWR(
    [GetCandidats, id],

    (query, id) => hasura.request(query),
    {
      initialData,
      revalidateOnMount: true,
    }
  );

  $("#sortable").sortable();
  $("#sortable").disableSelection();

  countTodos();

  // all done btn
  $("#checkAll").click(function () {
    AllDone();
  });

  //create todo
  $(".add-todo").on("keypress", function (e) {
    e.preventDefault;
    if (e.which == 13) {
      if ($(this).val() != "") {
        var todo = $(this).val();
        createTodo(todo);
        countTodos();
      } else {
        // some validation
      }
    }
  });
  // mark task as done
  $(".todolist").on(
    "change",
    '#sortable li input[type="checkbox"]',
    function () {
      if ($(this).prop("checked")) {
        var doneItem = $(this).parent().parent().find("label").text();
        $(this).parent().parent().parent().addClass("remove");
        done(doneItem);
        countTodos();
      }
    }
  );

  //delete done task from "already done"
  $(".todolist").on("click", ".remove-item", function () {
    removeItem(this);
  });

  // count tasks
  function countTodos() {
    var count = $("#sortable li").length;
    $(".count-todos").html(count);
  }

  //create task
  function createTodo(text) {
    var markup =
      '<li class="ui-state-default"><div class="checkbox"><label><input type="checkbox" value="" />' +
      text +
      "</label></div></li>";
    $("#sortable").append(markup);
    $(".add-todo").val("");
  }

  //mark task as done
  function done(doneItem) {
    var done = doneItem;
    var markup =
      "<li>" +
      done +
      '<button class="btn btn-default btn-xs pull-right  remove-item"><span class="glyphicon glyphicon-remove"></span></button></li>';
    $("#done-items").append(markup);
    $(".remove").remove();
  }

  //mark all tasks as done
  function AllDone() {
    var myArray = [];

    $("#sortable li").each(function () {
      myArray.push($(this).text());
    });

    // add to done
    for (i = 0; i < myArray.length; i++) {
      $("#done-items").append(
        "<li>" +
          myArray[i] +
          '<button class="btn btn-default btn-xs pull-right  remove-item"><span class="glyphicon glyphicon-remove"></span></button></li>'
      );
    }

    // myArray
    $("#sortable li").remove();
    countTodos();
  }

  //remove done task from list
  function removeItem(element) {
    $(element).parent().remove();
  }
  return (
    <Layout>
      <div className="page-content-wrapper">
        {" "}
        <div className="page-content" style={{ minHeight: 1500 }}>
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light bordered">
                <div class="portlet-title">
                  <div class="caption font-dark">
                    <i class="icon-settings font-dark"></i>
                    <span class="caption-subject bold uppercase">
                      {" "}
                      Individual column searching (select inputs)
                    </span>
                  </div>
                  <div class="actions">
                    <div
                      class="btn-group btn-group-devided"
                      data-toggle="buttons"
                    >
                      <label class="btn btn-transparent dark btn-outline btn-circle btn-sm active">
                        <input
                          type="radio"
                          name="options"
                          class="toggle"
                          id="option1"
                        />
                        Actions
                      </label>
                      <label class="btn btn-transparent dark btn-outline btn-circle btn-sm">
                        <input
                          type="radio"
                          name="options"
                          class="toggle"
                          id="option2"
                        />
                        Settings
                      </label>
                    </div>
                  </div>
                </div>
                <div class="portlet-body">
                  <div class="table-toolbar">
                    <div class="row">
                      <div class="col-md-6">
                        <div class="btn-group">
                          <button
                            id="sample_editable_1_2_new"
                            class="btn sbold green"
                          >
                            {" "}
                            Add New
                            <i class="fa fa-plus"></i>
                          </button>
                        </div>
                      </div>
                      <div class="col-md-6">
                        <div class="btn-group pull-right">
                          <button
                            class="btn green  btn-outline dropdown-toggle"
                            data-toggle="dropdown"
                          >
                            Tools
                            <i class="fa fa-angle-down"></i>
                          </button>
                          <ul class="dropdown-menu pull-right">
                            <li>
                              <a href="javascript:;">
                                <i class="fa fa-print"></i> Print{" "}
                              </a>
                            </li>
                            <li>
                              <a href="javascript:;">
                                <i class="fa fa-file-pdf-o"></i> Save as PDF{" "}
                              </a>
                            </li>
                            <li>
                              <a href="javascript:;">
                                <i class="fa fa-file-excel-o"></i> Export to
                                Excel{" "}
                              </a>
                            </li>
                          </ul>
                        </div>
                      </div>
                    </div>
                  </div>
                  {/* <table
                    class="table table-striped table-bordered table-hover table-checkable order-column"
                    id="sample_1_2"
                  >
                    <thead>
                      <tr>
                        <th>
                          <label class="mt-checkbox mt-checkbox-single mt-checkbox-outline">
                            <input
                              type="checkbox"
                              class="group-checkable"
                              data-set="#sample_1_2 .checkboxes"
                            />
                            <span></span>
                          </label>
                        </th>
                        <th> Username </th>
                        <th> Email </th>
                        <th> Status </th>
                        <th> Joined </th>
                        <th> Actions </th>
                      </tr>
                    </thead>
                    <tfoot>
                      <tr>
                        <th> </th>
                        <th> </th>
                        <th> </th>
