import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";

import { useAuthState, useAuthDispatch } from "../context/auth";

import Layout2 from "../pages/Components/Layout2";

export default function RegisterPage() {
  const router = useRouter();
  const { isAuthenticated } = useAuthState();
  const { login } = useAuthDispatch();
  const {
    handleSubmit,
    register,
    errors,
    formState: { isSubmitting },
    setError,
  } = useForm({ defaultValues: { save_last_seen: true } });

  useEffect(() => {
    if (isAuthenticated) {
      router.push("/");
    }
  }, [isAuthenticated]);

  if (isAuthenticated) return null;

  const onSubmit = async ({ email, password, save_last_seen }) => {
    try {
      await login({ email, password, save_last_seen });

      router.push("/");
    } catch ({ message }) {
      setError("email", {
        type: "manual",
        message,
      });
    }
  };

  return (
    <div>
      <div class="logo">
        <a href="index.html">
          <img
            src="../assets/pages/img/logo-big-white.png"
            style={{ height: 17 }}
            alt=""
          />{" "}
        </a>
      </div>
      <div class="content">
        <div class="form-title">
          <span class="form-title">Welcome.</span>
          <span class="form-subtitle">Please login.</span>
        </div>
        <form onSubmit={handleSubmit(onSubmit)} class="login-form">
          <div>
            <input
              type="email"
              name="email"
              id="email"
              ref={register({
                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                required: "You must provide a email.",
              })}
              placeholder="Your email"
            />
            {errors.email && <span>{errors.email.message}</span>}
          </div>
          <div>
            <input
              type="password"
              name="password"
              id="password"
              ref={register({
                required: "You must provide a password.",
              })}
              placeholder="Enter your password"
            />
            {errors.password && <span>{errors.password.message}</span>}
          </div>
          <div>
            <label htmlFor="save_last_seen">
              <input
                type="checkbox"
                id="save_last_seen"
                name="save_last_seen"
                ref={register}
              />
              Show as online to other users
            </label>
          </div>
          <div>
            <button type="submit" disabled={isSubmitting}>
              Login
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
