import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";

import { useAuthState, useAuthDispatch } from "../context/auth";
import { gql, hasuraAdminClient } from "../lib/hasura-admin-client";
import { hasuraUserClient } from "../lib/hasura-user-client";

const from = new Date().toISOString();

const GetUsers = gql`
  {
    User {
      Name
      id
      notifications_aggregate(where: { seen: { _eq: false } }) {
        aggregate {
          count
        }
      }
      notifications(where: { seen: { _eq: false } }) {
        id
        description
      }
    }
  }
`;
const GetTodos = gql`
  {
    Todo {
      Description
      Name
      Date
      User {
        Name
        id
      }
    }
  }
`;
const GetUserById = gql`
  query GetUserById($user_id: Int!) {
    User_by_pk(id: $user_id) {
      id
      notifications_aggregate(where: { seen: { _eq: false } }) {
        aggregate {
          count
        }
      }
      notifications(where: { seen: { _eq: false } }) {
        id
        description
      }
    }
  }
`;
const AddNotification = gql`
  mutation MyMutation($user_id: Int!) {
    insert_notification_one(
      object: {
        user_id: $user_id
        name: "ttt"
        seen: false
        description: "tttttt"
      }
    ) {
      user_id
      seen
      name
      id
      description
    }
  }
`;
const insertMultiple = gql`
  mutation MyMutation(
    $Description: String!
    $Name: String!
    $Date: timestamptz = ""
    $Description1: String!
    $Name1: String!
    $Date1: timestamptz = ""
    $Description2: String!
    $Name2: String!
    $Date2: timestamptz = ""
    $Description3: String!
    $Name3: String!
    $Date3: timestamptz = ""
    $Description4: String!
    $Name4: String!
    $Date4: timestamptz = ""
  ) {
    insert_Todo(
      objects: [
        { Description: $Description, Name: $Name, Date: $Date }
        { Description: $Description1, Name: $Name1, Date: $Date1 }
        { Description: $Description2, Name: $Name1, Date: $Date1 }
        { Description: $Description3, Name: $Name3, Date: $Date3 }
        { Description: $Description4, Name: $Name4, Date: $Date4 }
      ]
    ) {
      affected_rows
    }
  }
`;
const InsertTask = gql`
  mutation InsertTask(
    $Description: String!
    $Name: String!
    $from: timestamptz!
    $user_id: Int!
  ) {
    insert_Task_one(
      object: {
        Description: $Description
        Name: $Name
        Date: $from
        user_id: $user_id
      }
    ) {
      Description
      Date
      Name
      user_id
    }
  }
`;

export const getStaticProps = async () => {
  const { User } = await hasuraAdminClient.request(GetUsers);

  return {
    props: {
      User,
    },
  };
};

export default function Todos({ User }) {
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const hasura = hasuraUserClient();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const { updateUser } = useAuthDispatch();
  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  if (!isAuthenticated) return null;

  const onSubmit = async ({ Description, Name, user_id }) => {
    console.log(from);
    try {
      const { insert_Todo_one } = await hasura.request(InsertTask, {
        Description,
        from,
        Name,
        user_id,
      });
      const { insert_notification_one } = await hasura.request(
        AddNotification,
        {
          user_id,
        }
      );
      if (isAuthenticated) {
        const { User_by_pk } = await hasura.request(GetUserById, {
          user_id: user.id,
        });
        updateUser(User_by_pk);
      }
      router.push(`/CandidatList`);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div class="form-group">
                          <label class="control-label">Name</label>
                          <input
                            name="Name"
                            id="Name"
                            type="text"
                            placeholder="Todo Name"
                            class="form-control"
                            ref={register({
                              required:
                                "You must select a category for your thread",
                            })}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Description</label>
                          <textarea
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="Description"
                            class="form-control"
                            ref={register({
                              required:
                                "You must select a category for your thread",
                            })}
                          />{" "}
                        </div>
                        <select
                          name="user_id"
                          id="user_id"
                          ref={register({
                            required:
                              "You must select a category for your thread",
                          })}
                          className="appearance-none bg-transparent w-full"
                        >
                          {User.map(({ id, Name }) => (
                            <React.Fragment key={id}>
                              <option value={id}>{Name}</option>
                            </React.Fragment>
                          ))}
                        </select>{" "}
                        <div class="form-group mt-repeater">
                          <div data-repeater-list="group-b">
                            <div class="mt-repeater-item">
                              <label class="control-label">Mobile Number</label>
                              <input
                                type="text"
                                placeholder="+1 646 580 DEMO (6284)"
                                class="form-control"
                              />{" "}
                            </div>
                            <div
                              data-repeater-item
                              class="mt-repeater-item mt-overflow"
                            >
                              <label class="control-label">
                                Additional Contact Number
                              </label>
                              <div class="mt-repeater-cell">
                                <input
                                  type="text"
                                  placeholder="+1 646 580 DEMO (6284)"
                                  class="form-control mt-repeater-input-inline"
                                />
                                <a
                                  href="javascript:;"
                                  data-repeater-delete
                                  class="btn btn-danger mt-repeater-delete mt-repeater-del-right mt-repeater-btn-inline"
                                >
                                  <i class="fa fa-close"></i>
                                </a>
                              </div>
                            </div>
                          </div>
                          <a
                            href="javascript:;"
                            data-repeater-create
                            class="btn btn-success mt-repeater-add"
                          >
                            <i class="fa fa-plus"></i> Add new contact number
                          </a>
                        </div>
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                          <a href="#" class="btn default">
                            {" "}
                            Cancel{" "}
                          </a>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>{" "}
    </div>
  );
}
