import { useState } from "react";
import { useRouter } from "next/router";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { hasuraAdminClient, gql } from "../../lib/hasura-admin-client";
import useSWR, { mutate } from "swr";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import { useForm, Controller } from "react-hook-form";
import bcrypt from "bcryptjs";

const GetUsersId = gql`
  {
    User {
      id
      Last_Name
      Name
      Avatar
      email
      Role
      Phone_Number
      Domain
      password
    }
  }
`;

const GetUserById = gql`
  query GetUserById($id: Int!) {
    User_by_pk(id: $id) {
      id
      Name
      Avatar
      email
      Role
      Phone_Number
      Last_Name
      Domain
      password
      Projects_aggregate {
        aggregate {
          count
        }
      }
      Tasks_aggregate {
        aggregate {
          count
        }
      }
      LinkedIn
      Github
      Site
    }
  }
`;

const CountTasksByUserId = gql`
  query MyQuery($_eq: Int!) {
    Todo_aggregate(where: { user_id: { _eq: $_eq } }) {
      aggregate {
        count(columns: Completed)
      }
    }
  }
`;
//tobedone
//Countprojects
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(GetUsersId);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;
  const initialData = await hasura.request(GetUserById, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
const UpdateUser = gql`
  mutation update_User_by_pk(
    $id: Int!
    $Name: String!
    $Last_Name: String!
    $email: String!
    $Phone_Number: String!
    $LinkedIn: String!
    $Github: String!
    $Site: String!
  ) {
    update_User_by_pk(
      pk_columns: { id: $id }
      _set: {
        Name: $Name
        Last_Name: $Last_Name
        email: $email
        Phone_Number: $Phone_Number
        LinkedIn: $LinkedIn
        Github: $Github
        Site: $Site
      }
    ) {
      id
      Name
      Last_Name
      email
      Phone_Number
      LinkedIn
      Github
      Site
    }
  }
`;
const UpdatePassword = gql`
  mutation update_User_by_pk($id: Int!, $password: String!) {
    update_User_by_pk(pk_columns: { id: $id }, _set: { password: $password }) {
      id
    }
  }
`;
export default function PrivatePage({ initialData }) {
  const [image, setImage] = useState(null);
  const { updateUser } = useAuthDispatch();
  const hasura = hasuraUserClient();

  const [createObjectURL, setCreateObjectURL] = useState(null);
  const router = useRouter();
  const { id } = router.query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  const uploadToClient = (event) => {
    if (event.target.files && event.target.files[0]) {
      const i = event.target.files[0];
      setImage(i);
      setCreateObjectURL(URL.createObjectURL(i));
    }
  };
  const {
    register: register2,
    errors: errors2,
    handleSubmit: handleSubmit2,
  } = useForm();

  const uploadToServer = async () => {
    const body = new FormData();
    body.append("file", image);
    const response = await fetch("/api/file/", {
      method: "POST",
      body,
    });

    const UploadImage = gql`
      mutation MyMutation($Avatar: String!, $id: Int!) {
        update_User_by_pk(pk_columns: { id: $id }, _set: { Avatar: $Avatar }) {
          Avatar
          Name
        }
      }
    `;
    try {
      const { update_User_by_pk } = await hasura.request(UploadImage, {
        id,
        Avatar: image.name,
      });
      updateUser(update_User_by_pk);
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
    return;
  };
  const { data, mutate } = useSWR(
    [GetUserById, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  const ChangePassword = async ({ password, confirmpassword }) => {
    try {
      const id = data.User_by_pk.id;

      const salt = await bcrypt.genSalt();
      if (password == confirmpassword) {
        const { update_User_by_pk } = await hasura.request(UpdatePassword, {
          id,
          password: await bcrypt.hash(password, salt),
        });

        mutate({
          ...data,
          ...update_User_by_pk,
        });
        window.location.reload();
      } else {
        alert("Passwords don't match");
      }
    } catch (err) {
      console.log(err);
    }
  };
  const UpdateUserB = async ({
    Name,
    Last_Name,
    email,
    Phone_Number,
    LinkedIn,
    Github,
    Site,
  }) => {
    try {
      const id = data.User_by_pk.id;

      const { update_User_by_pk } = await hasura.request(UpdateUser, {
        id,
        Name,
        Last_Name,
        email,
        Phone_Number,
        LinkedIn,
        Github,
        Site,
      });

      mutate({
        ...data,
        ...update_User_by_pk,
      });
      updateUser(update_User_by_pk);

      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div className="row">
          <div className="col-md-12">
            <div className="col-md-6">
              <div className="profile-sidebar">
                <div className="portlet light profile-sidebar-portlet ">
                  <div className="profile-userpic" style={{ display: "block" }}>
                    <img
                      style={{ width: 100 }}
                      src={`../../${data.User_by_pk.Avatar}`}
                      class="img-fluid img-thumbnail "
                      alt=""
                    />{" "}
                  </div>{" "}
                  <div className="profile-usertitle">
                    <div className="profile-usertitle-name">
                      {" "}
                      {data.User_by_pk.Name} {data.User_by_pk.Last_Name}{" "}
                    </div>
                    <div className="profile-usertitle-job">
                      {" "}
                      {data.User_by_pk.Domain}{" "}
                    </div>
                  </div>{" "}
                </div>
                <div className="portlet light ">
                  <div className="row list-separated profile-stat">
                    <div className="col-md-4 col-sm-4 col-xs-6">
                      <div className="uppercase profile-stat-title">
                        {" "}
                        {
                          data.User_by_pk.Projects_aggregate.aggregate.count
                        }{" "}
                      </div>
                      <div className="uppercase profile-stat-text">
                        {" "}
                        Live Projects{" "}
                      </div>
                    </div>
                    <div className="col-md-4 col-sm-4 col-xs-6">
                      <div className="uppercase profile-stat-title">
                        {" "}
                        {data.User_by_pk.Tasks_aggregate.aggregate.count}{" "}
                      </div>
                      <div className="uppercase profile-stat-text"> Tasks </div>
                    </div>
                    <div className="col-md-4 col-sm-4 col-xs-6">
                      <div className="uppercase profile-stat-title"> 61 </div>
                      <div className="uppercase profile-stat-text">
                        {" "}
                        Uploads{" "}
                      </div>
                    </div>
                  </div>
                  <div>
                    <h4 className="profile-desc-title">
                      About {data.User_by_pk.Name}
                    </h4>
                    <div className="margin-top-20 profile-desc-link">
                      {" "}
                      <i class="fa fa-linkedin-square" aria-hidden="true"></i>
                      <a href={data.User_by_pk.LinkedIn}>
                        {data.User_by_pk.LinkedIn}
                      </a>
                    </div>
                    <div className="margin-top-20 profile-desc-link">
                      <i class="fa fa-github-square" aria-hidden="true"></i>
                      <a href={data.User_by_pk.Github}>
                        {data.User_by_pk.Github}
                      </a>
                    </div>
                    <div className="margin-top-20 profile-desc-link">
                      <i class="fa fa-globe" aria-hidden="true"></i>
                      <a href={data.User_by_pk.Site}>{data.User_by_pk.Site}</a>
                    </div>{" "}
                    <div className="margin-top-20 profile-desc-link">
                      <img
                        src="../../assets/global/img/pdf.png"
                        href="#myModal"
                        style={{ maxWidth: 35 }}
                      />
                      <a
                        href="#myModal"
                        role="button"
                        class="btn red btn-outline"
                        data-toggle="modal"
                      >
                        {" "}
                        View File{" "}
                      </a>
                    </div>
                    <div
                      id="myModal"
                      class="modal fade"
                      role="dialog"
                      aria-hidden="true"
                    >
                      <div class="modal-dialog">
                        <div class="modal-content">
                          <div class="modal-header">
                            <a
                              href="javascript:;"
                              class="close"
                              data-dismiss="modal"
                              aria-hidden="true"
                            ></a>
                            <h4 class="modal-title">Bootstrap switches</h4>
                          </div>
                          <div class="modal-body">
                            <embed
                              src="../../assets/files/article.pdf"
                              width="600"
                              height="500"
                              type="application/pdf"
                            />
                          </div>
                          <div class="modal-footer">
                            <a
                              href="javascript:;"
                              class="btn grey-salsa btn-outline"
                              data-dismiss="modal"
                            >
                              Close
                            </a>
                            <a href="javascript:;" class="btn green">
                              <i class="fa fa-check"></i> Save changes
                            </a>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="col-md-6">
              <div className="profile-content">
                {" "}
                <div className="row">
                  <div className="col-md-12">
                    <div className="portlet light ">
                      <div className="portlet-title tabbable-line">
                        <div className="caption caption-md">
                          <i className="icon-globe theme-font hide"></i>
                          <span className="caption-subject font-blue-madison bold uppercase">
                            Profile Account
                          </span>
                        </div>
                        <ul className="nav nav-tabs">
                          <li className="active">
                            <a href="#tab_1_1" data-toggle="tab">
                              Personal Info
                            </a>
                          </li>
                          <li>
                            <a href="#tab_1_2" data-toggle="tab">
                              Change Avatar
                            </a>
                          </li>
                          <li>
                            <a href="#tab_1_3" data-toggle="tab">
                              Change Password
                            </a>
                          </li>
                        </ul>
                      </div>
                      <div className="portlet-body">
                        <div className="tab-content">
                          <div className="tab-pane active" id="tab_1_1">
                            <form onSubmit={handleSubmit(UpdateUserB)}>
                              <div className="form-group">
                                <label className="control-label">
                                  First Name
                                </label>
