import useSWR, { mutate } from "swr";
import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import Link from "next/link";
import gql from "graphql-tag";

import { hasuraUserClient } from "../../lib/hasura-user-client";

const Created = new Date().toISOString();

const InsertProject = gql`
  mutation InsertProject(
    $Description: String!
    $Title: String!
    $Created: timestamptz!
  ) {
    insert_Project_one(
      object: { Description: $Description, Title: $Title, created_at: $Created }
    ) {
      Description
      created_at
      Title
    }
  }
`;

export default function Projects({ initialData }) {
  const hasura = hasuraUserClient();
  const router = useRouter();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();

  const onSubmit = async ({ Description, Title }) => {
    try {
      const { insert_Project_one } = await hasura.request(InsertProject, {
        Description,
        Created,
        Title,
      });

      router.push(`/Project/List`);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div class="form-group">
                          <label class="control-label">Project Title</label>
                          <input
                            name="Title"
                            id="Title"
                            type="text"
                            placeholder="Project Title"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">
                            Project Description
                          </label>
                          <textarea
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>

                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                          <a href="#" class="btn default">
                            {" "}
                            Cancel{" "}
                          </a>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>{" "}
    </div>
  );
}
