import React from 'react'
import Header from './Header/Header'
import Footer from './Footer/Footer'
import Sidebar from './Sidebar/Sidebar'
import Notification from '../Notification'
import gql from "graphql-tag";
import { hasuraUserClient } from "../../lib/hasura-user-client";

const Notification_count = gql`
  query MyQuery {
    notification_aggregate {
      aggregate {
        count(columns: id)
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(Notification_count);
  const c = initialData.notification_aggregate.aggregate.count;

  return {
    props: {
      initialData,
      c,
    },
    revalidate: 1,
  };
};
console.log(c)
export default function Layout({ children}) {
    return (
      <>    <div class="page-container">

        <Header/>
        <Sidebar/>   
          { children }
         <Footer />
         </div>
      </>
    );
  }
