import useSWR, { mutate } from "swr";
import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import React, { useState } from "react";
import { Button, Alert } from "react-bootstrap";
import Link from "next/link";
import gql from "graphql-tag";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { format, intervalToDuration, parseISO, set } from "date-fns";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";
import "@fullcalendar/list/main.css";
import { TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import interactionPlugin from "@fullcalendar/interaction";
import timeGridPlugin from "@fullcalendar/timegrid";
import listPlugin from "@fullcalendar/list";
import { hasuraUserClient } from "../../lib/hasura-user-client";
const today = new Date().toISOString().substring(0, 19);
const mindate = new Date().toISOString().substring(0, 10);
import { differenceInHours } from "date-fns";
import { differenceInDays } from "date-fns";
// import Alert from "@material-ui/lab/Alert";
import AlertTitle from "@material-ui/lab/AlertTitle";
import { isWithinInterval } from "date-fns";
import styled from "styled-components";

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    flexWrap: "wrap",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
    fontSize: 60,
  },
}));
const Dot = styled.span`
  padding: 20px;
  background-color: #f44336;
  color: white;
  margin-left: 15px;
  color: white;
  font-weight: bold;
  float: right;
  font-size: 22px;
  line-height: 20px;
  cursor: pointer;
  transition: 0.3s;
`;
const Dot2 = styled.span`
  .closebtn:hover {
    color: black;
  }
`;
const useStyless = makeStyles((theme) => ({
  hover: {
    color: black,
  },
}));
const GetUsersId = gql`
  {
    User {
      id
      Last_Name
      Name
      Avatar
      email
      Role
      Phone_Number
      Domain
      password
      File
    }
  }
`;
const GetUserById = gql`
  query GetUserById($id: Int!) {
    User_by_pk(id: $id) {
      id
      Name
      Avatar
      email
      Role
      Phone_Number
      Last_Name
      Domain
      password
      File
      Tasks_aggregate {
        aggregate {
          count
        }
      }
      LinkedIn
      Github
      Site
    }
  }
`;
const getLeave = gql`
  query MyQuery($id: Int!) {
    leave(where: { user_id: { _eq: $id } }) {
      id
      reason
      start_date
      end_date
      count_d
      status
    }
  }
`;
const count = gql`
  query MyQuery {
    leave_aggregate {
      aggregate {
        count
      }
    }
  }
`;
const AddNotification = gql`
  mutation MyMutation {
    insert_notification_one(
      object: {
        name: "Leave"
        seen: false
        description: "new Leave demand is submitted, please verify it"
        user_id: 42
      }
    ) {
      seen
      name
      id
      description
      user_id
    }
  }
`;

const insertLeave = gql`
  mutation MyMutation(
    $user_id: Int!
    $end_date: timestamp!
    $start_date: timestamp!
    $reason: String!
    $count_d: Int!
  ) {
    insert_leave_one(
      object: {
        user_id: $user_id
        start_date: $start_date
        end_date: $end_date
        status: 0
        reason: $reason
        count_d: $count_d
      }
    ) {
      start_date
      end_date
      count_d
    }
  }
`;
const sumLeave = gql`
  query MyQuery($id: Int!) {
    leave_aggregate(where: { user_id: { _eq: $id } }) {
      aggregate {
        sum {
          count_d
        }
      }
    }
  }
`;

export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(GetUsersId);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(getLeave, { id });
  const DataCount = await hasura.request(sumLeave, { id });
  const Count = await hasura.request(count);

  return {
    props: {
      initialData,
      DataCount,
      id,
      Count,
    },
    revalidate: 1,
  };
};

export default function leaveupdate({ initialData, id, DataCount, Count }) {
  const { isAuthenticated, user } = useAuthState();

  const classes = useStyles();
  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  const sumLeave = DataCount.leave_aggregate.aggregate.sum.count_d;
  const [hide, sethide] = useState(true);
  const [hidef, sethidef] = useState(false);

  function toggle() {
    sethide(!hide);
  }
  function togglefile() {
    sethidef(true);
  }
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const hasura = hasuraUserClient();
  const router = useRouter();
  const { data } = useSWR(getLeave, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const t = data.leave.map(({ id, reason, start_date, end_date }) => {
    const start = format(parseISO(start_date), "yyyy-MM-dd");
    return start;
  });
  const e = data.leave.map(({ id, reason, start_date, end_date }) => {
    const end = format(parseISO(end_date), "yyyy-MM-dd");
    return end;
  });
  const c = Count.leave_aggregate.aggregate.count;
  const d = isWithinInterval(new Date(2014, 0, 3), {
    start: new Date(2014, 0, 1),
    end: new Date(2014, 0, 7),
  });
  console.log(d);
  const onSubmit = async ({ reason, start_date, end_date }) => {
    try {
      const user_id = id;
      const count_d = differenceInDays(
        Date.parse(end_date),
        Date.parse(start_date)
      );
      if (count_d < 1) {
        alert("please enter a valid period");
      } else if (sumLeave >= 20 || count_d > sumLeave) {
        alert(
          "please contact the manager , you looks like you got no more  days left on your calendar or you may surpassed your limit"
        );
      } else if (t.includes(start_date) || e.includes(end_date)) {
        alert(
          "you already signed for those days , please try again or contact the admin"
        );
      } else {
        const { insert_leave_one } = await hasura.request(insertLeave, {
          reason,
          start_date,
          count_d,
          end_date,
          user_id,
        });
        const { insert_notification_one } = await hasura.request(
          AddNotification
        );
        alert(
          "your request has been submitted, please wait until we review it"
        );
        window.location.reload();
      }
    } catch (err) {
      console.log(err);
    }
  };

  const cancelrequest = async (id) => {
    const Delete = gql`
      mutation MyMutation($id: Int!) {
        delete_leave(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;
    if (confirm("are you sure you want to cancel this request ")) {
      try {
        await hasura.request(Delete, { id });
        mutate();
        window.location.reload();
      } catch (error) {
        console.error(error);
      }
    } else return null;
  };
  const handleDateClick = (arg) => {
    // bind with an arrow function
    alert(`date ${arg.dateStr} marked`);
    // alert("Coordinates: " + arg.jsEvent.pageX + "," + arg.jsEvent.pageY);
    // alert("Current view: " + arg.view.type);
    // change the day's background color just for fun
    arg.dayEl.style.backgroundColor = "#FF5C5C";
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        {/* <h1 class="page-title"> Calendar</h1> */}
        test
      </div>
    </div>
  );
}
