import useSWR, { mutate } from "swr";
import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import React, { useState } from "react";

import Link from "next/link";
import gql from "graphql-tag";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import { hasuraUserClient } from "../../lib/hasura-user-client";

const Created = new Date().toISOString();

const InsertProject = gql`
  mutation InsertProject(
    $Description: String!
    $Title: String!
    $Created: timestamptz!
    $delivery_date: timestamptz!
  ) {
    insert_Project_one(
      object: {
        Description: $Description
        Title: $Title
        created_at: $Created
        delivery_date: $delivery_date
      }
    ) {
      Description
      created_at
      Title
    }
  }
`;

export default function Projects({ initialData }) {
  const [startDate, setStartDate] = useState(new Date());
  const hasura = hasuraUserClient();
  const router = useRouter();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();

  const onSubmit = async ({ Description, Title }) => {
    console.log(startDate);
    console.log(Created);
    const delivery_date = startDate;
    try {
      const { insert_Project_one } = await hasura.request(InsertProject, {
        Description,
        Created,
        Title,
        delivery_date,
      });

      router.push(`/Project/List`);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {" "}
                        <div class="form-group">
                          <label class="control-label">Client</label>
                          <input
                            name="Title"
                            id="Title"
                            type="text"
                            placeholder="Client"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Project Title</label>
                          <input
                            name="Title"
                            id="Title"
                            type="text"
                            placeholder="Project Title"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">
                            Project Description
                          </label>
                          <textarea
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>{" "}
                        <div class="form-group">
                          <label class="control-label">additonal info</label>
                          <textarea
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="additonal info"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <DatePicker
                          selected={startDate}
                          onChange={(date) => setStartDate(date)}
                        />{" "}
                        <br />
                        <br />
                        <DatePicker
                          selected={startDate}
                          onChange={(date) => setStartDate(date)}
                        />{" "}
                        <div>
                          {" "}
                          <br />
                          <br />
                          <span className="btn default btn-file">
                            <input className="mb-3" type="file" name="File" />
                          </span>
                        </div>
                        <br />
                        <br />
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                          <a href="#" class="btn default">
                            {" "}
                            Cancel{" "}
                          </a>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>{" "}
    </div>
  );
}
