import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { useForm, Controller } from "react-hook-form";
import Layout from "./Components/Layout";

import { useAuthState } from "../context/auth";
import { gql, hasuraAdminClient } from "../lib/hasura-admin-client";
import { hasuraUserClient } from "../lib/hasura-user-client";

const GetUsers = gql`
  {
    User {
      Name
      id
    }
  }
`;

const InsertTask = gql`
  mutation InsertTask(
    $Email: String!
    $Last_Name: String!
    $Name: String!
    $domain: String!
    $number: String!
  ) {
    insert_Candidate_one(
      object: {
        Email: $Email
        Last_Name: $Last_Name
        Name: $Name
        domain: $domain
        phone_number: $number
      }
    ) {
      Email
      Last_Name
      Name
      domain
      id
      number
    }
  }
`;

export const getStaticProps = async () => {
  const { User } = await hasuraAdminClient.request(GetUsers);

  return {
    props: {
      User,
    },
  };
};

export default function Candidat() {
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const hasura = hasuraUserClient();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const [selectedTab, setSelectedTab] = useState("write");

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  if (!isAuthenticated) return null;

  const onSubmit = async ({ Email, Last_Name, Name, domain, number }) => {
    console.log(number);
    try {
      const { insert_Candidate_one } = await hasura.request(InsertTask, {
        Email,
        Last_Name,
        Name,
        domain,
        number,
      });

      router.push(`/Candidat/${insert_Candidate_one.id}`);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <>
      <Layout>
        <div className="page-content-wrapper">
          <div className="page-content" style={{ minHeight: 1500 }}>
            <div class="profile-content">
              <div class="row">
                <div class="col-md-12">
                  <div class="portlet light ">
                    <div class="portlet-body">
                      <div class="tab-content">
                        <div class="tab-pane active" id="tab_1_1">
                          <form onSubmit={handleSubmit(onSubmit)}>
                            <div class="form-group">
                              <label class="control-label">First Name</label>
                              <input
                                name="Name"
                                id="Name"
                                type="text"
                                placeholder="First Name"
                                class="form-control"
                                ref={register({
                                  required:
                                    "You must select a category for your thread",
                                })}
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Last Name</label>
                              <input
                                name="Last_Name"
                                id="Last_Name"
                                type="text"
                                placeholder="Last Name"
                                class="form-control"
                                ref={register({
                                  required:
                                    "You must select a category for your thread",
                                })}
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Mobile Number</label>
                              <input
                                name="number"
                                id="number"
                                type="text"
                                placeholder="+1 646 580 DEMO (6284)"
                                class="form-control"
                                ref={register({
                                  required:
                                    "You must select a category for your thread",
                                })}
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Email</label>
                              <input
                                name="Email"
                                id="Email"
                                type="email"
                                placeholder="Design, Web etc."
                                class="form-control"
                                ref={register({
                                  required:
                                    "You must select a category for your thread",
                                })}
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Domain</label>
                              <input
                                name="domain"
                                id="domain"
                                type="text"
                                placeholder="Web Developer"
                                class="form-control"
                                ref={register({
                                  required:
                                    "You must select a category for your thread",
                                })}
                              />{" "}
                            </div>
                            <div class="margiv-top-10">
                              <button
                                type="submit"
                                class="btn green"
                                disabled={isSubmitting}
                              >
                                {" "}
                                Save Changes{" "}
                              </button>
                              <a href="#" class="btn default">
                                {" "}
                                Cancel{" "}
                              </a>
                            </div>
                          </form>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>{" "}
        </div>
      </Layout>
    </>
  );
}
