import useSWR, { mutate } from "swr";

import gql from "graphql-tag";
import { format, parseISO } from "date-fns";
import { hasuraUserClient } from "../lib/hasura-user-client";
import Link from "next/link";
const today = new Date();

const GetUsers = gql`
  query MyQuery {
    User {
      id
      Name
      email
      created_at
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(GetUsers);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
const pdf = async () => {
  const res = await fetch("/api/pdf", {
    method: "POST",
    headers: {
      "Content-Type": "application/pdf",
    },
  });
};
export default function MembersPage({ initialData }) {
  const hasura = hasuraUserClient();

  const { data } = useSWR(GetUsers, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const deleteATodo = async (id) => {
    const query = gql`
      mutation delete_User($id: Int!) {
        delete_User(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;

    try {
      await hasura.request(query, { id });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="row">
          <div class="col-md-12">
            <div class="portlet light bordered">
              <div class="portlet-title">
                <div class="caption font-dark">
                  <i class="icon-settings font-dark"></i>
                  <span class="caption-subject bold uppercase">
                    {" "}
                    Users (Employees)
                  </span>
                </div>
              </div>
              <div class="portlet-body">
                <div class="table-toolbar">
                  <div class="row">
                    <div class="col-md-6">
                      <div class="btn-group">
                        <button
                          id="sample_editable_1_2_new"
                          class="btn sbold green"
                        >
                          {" "}
                          Register new user
                          <i class="fa fa-plus"></i>
                        </button>
                      </div>
                    </div>
                    <div class="col-md-6">
                      <div class="btn-group pull-right">
                        <button
                          class="btn green  btn-outline dropdown-toggle"
                          data-toggle="dropdown"
                        >
                          Tools
                          <i class="fa fa-angle-down"></i>
                        </button>
                        <ul class="dropdown-menu pull-right">
                          <li>
                            <a href="#">
                              <i class="fa fa-print"></i> Print{" "}
                            </a>
                          </li>
                          <li>
                            <a href="#">
                              <i class="fa fa-file-pdf-o"></i> Save as PDF{" "}
                            </a>
                          </li>
                          <li>
                            <a href="#">
                              <i class="fa fa-file-excel-o"></i> Export to Excel{" "}
                            </a>
                          </li>
                        </ul>
                      </div>
                    </div>
                  </div>
                </div>
                <table
                  class="table table-striped table-bordered table-hover table-checkable order-column"
                  id="sample_1_2"
                >
                  <thead>
                    <tr>
                      <th>
                        <label class="mt-checkbox mt-checkbox-single mt-checkbox-outline">
                          <input
                            type="checkbox"
                            class="group-checkable"
                            data-set="#sample_1_2 .checkboxes"
                          />
                          <span></span>
                        </label>
                      </th>
                      <th> Username </th>
                      <th> Email </th>
                      <th> Status </th>
                      <th> Joined </th>
                      <th> Actions </th>
                    </tr>
                  </thead>
                  <tfoot>
                    <tr>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                    </tr>
                  </tfoot>
                  <tbody>
                    {data.User.map(({ id, Name, email, created_at }) => {
                      const formattedJoinedAt = format(
                        parseISO(created_at),
                        "MM/dd/yyyy"
                      );
                      return (
                        <tr key={id} class="odd gradeX">
                          <td>
                            <label class="mt-checkbox mt-checkbox-single mt-checkbox-outline">
                              <input
                                type="checkbox"
                                class="checkboxes"
                                value="1"
                              />
                              <span></span>
                            </label>
                          </td>
                          <td>{Name}</td>
                          <td>
                            <a href="mailto:shuxer@gmail.com"> {email}</a>
                          </td>
                          <td></td>
                          <td class="center">{formattedJoinedAt}</td>
                          <td>
                            <div class="btn-group">
                              <button
                                class="btn btn-xs green dropdown-toggle"
                                type="button"
                                data-toggle="dropdown"
                                aria-expanded="false"
                              >
                                {" "}
                                Actions
                                <i class="fa fa-angle-down"></i>
                              </button>
                              <ul class="dropdown-menu pull-left" role="menu">
                                <li>
                                  <a
                                    className="closeBtn"
                                    onClick={() => deleteATodo(id)}
                                  >
                                    Delete User
                                  </a>
                                </li>
                              </ul>
                            </div>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
