import { gql } from "graphql-request";
import { useRouter } from "next/router";
import useSWR from "swr";
import { hasuraUserClient } from "../lib/hasura-user-client";
import Layout from "./Components/Layout";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import { format, parseISO } from "date-fns";
import "@fullcalendar/timegrid/main.css";
import "@fullcalendar/list/main.css";
import interactionPlugin from "@fullcalendar/interaction";
import timeGridPlugin from "@fullcalendar/timegrid";
import listPlugin from "@fullcalendar/list";
const Users = gql`
  query MyQuery {
    User_aggregate {
      aggregate {
        count
      }
    }
  }
`;
const GetEvents = gql`
  query MyQuery {
    Event {
      id
      Title
      description
      start_date
    }
  }
`;
const Projects = gql`
  query Query {
    Project_aggregate {
      aggregate {
        count
      }
    }
  }
`;
const Candidats = gql`
  query Query {
    Candidate_aggregate(where: { Status: { _eq: 0 } }) {
      aggregate {
        count
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(Users);
  const ProjectData = await hasura.request(Projects);
  const CandidatsData = await hasura.request(Candidats);
  const Events = await hasura.request(GetEvents);

  return {
    props: {
      initialData,
      ProjectData,
      CandidatsData,
      Events,
    },
    revalidate: 1,
  };
};

export default function Home({
  initialData,
  ProjectData,
  CandidatsData,
  Events,
}) {
  const hasura = hasuraUserClient();
  const router = useRouter();
  console.log(ProjectData);
  const { data } = useSWR(Users, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const { dataEvents } = useSWR(GetEvents, (query) => hasura.request(query), {
    Events,
    revalidateOnMount: true,
  });
  return (
    <>
      <div className="page-content-wrapper">
        <div className="page-content" style={{ minHeight: 1500 }}>
          <div class="theme-panel hidden-xs hidden-sm">
            <div class="toggler"> </div>
            <div class="toggler-close"> </div>
            <div class="theme-options">
              <div class="theme-option theme-colors clearfix">
                <span> THEME COLOR </span>
                <ul>
                  <li
                    class="color-default current tooltips"
                    data-style="default"
                    data-container="body"
                    data-original-title="Default"
                  >
                    {" "}
                  </li>
                  <li
                    class="color-darkblue tooltips"
                    data-style="darkblue"
                    data-container="body"
                    data-original-title="Dark Blue"
                  >
                    {" "}
                  </li>
                  <li
                    class="color-blue tooltips"
                    data-style="blue"
                    data-container="body"
                    data-original-title="Blue"
                  >
                    {" "}
                  </li>
                  <li
                    class="color-grey tooltips"
                    data-style="grey"
                    data-container="body"
                    data-original-title="Grey"
                  >
                    {" "}
                  </li>
                  <li
                    class="color-light tooltips"
                    data-style="light"
                    data-container="body"
                    data-original-title="Light"
                  >
                    {" "}
                  </li>
                  <li
                    class="color-light2 tooltips"
                    data-style="light2"
                    data-container="body"
                    data-html="true"
                    data-original-title="Light 2"
                  >
                    {" "}
                  </li>
                </ul>
              </div>
              <div class="theme-option">
                <span> Layout </span>
                <select class="layout-option form-control input-sm">
                  <option value="fluid" selected="selected">
                    Fluid
                  </option>
                  <option value="boxed">Boxed</option>
                </select>
              </div>
              <div class="theme-option">
                <span> Header </span>
                <select class="page-header-option form-control input-sm">
                  <option value="fixed" selected="selected">
                    Fixed
                  </option>
                  <option value="default">Default</option>
                </select>
              </div>
              <div class="theme-option">
                <span> Top Menu Dropdown</span>
                <select class="page-header-top-dropdown-style-option form-control input-sm">
                  <option value="light" selected="selected">
                    Light
                  </option>
                  <option value="dark">Dark</option>
                </select>
              </div>
              <div class="theme-option">
                <span> Sidebar Mode</span>
                <select class="sidebar-option form-control input-sm">
                  <option value="fixed">Fixed</option>
                  <option value="default" selected="selected">
                    Default
                  </option>
                </select>
              </div>
              <div class="theme-option">
                <span> Sidebar Menu </span>
                <select class="sidebar-menu-option form-control input-sm">
                  <option value="accordion" selected="selected">
                    Accordion
                  </option>
                  <option value="hover">Hover</option>
                </select>
              </div>
              <div class="theme-option">
                <span> Sidebar Style </span>
                <select class="sidebar-style-option form-control input-sm">
                  <option value="default" selected="selected">
                    Default
                  </option>
                  <option value="light">Light</option>
                </select>
              </div>
              <div class="theme-option">
                <span> Sidebar Position </span>
                <select class="sidebar-pos-option form-control input-sm">
                  <option value="left" selected="selected">
                    Left
                  </option>
                  <option value="right">Right</option>
                </select>
              </div>
              <div class="theme-option">
                <span> Footer </span>
                <select class="page-footer-option form-control input-sm">
                  <option value="fixed">Fixed</option>
                  <option value="default" selected="selected">
                    Default
                  </option>
                </select>
              </div>
            </div>
          </div>
          <div class="page-bar">
            <ul class="page-breadcrumb">
              <li>
                <a href="index.html">Home</a>
                <i class="fa fa-circle"></i>
              </li>
              <li>
                <span>Dashboard</span>
              </li>
            </ul>
            <div class="page-toolbar">
              <div
                id="dashboard-report-range"
                class="pull-right tooltips btn btn-sm"
                data-container="body"
                data-placement="bottom"
                data-original-title="Change dashboard date range"
              >
                <i class="icon-calendar"></i>&nbsp;
                <span class="thin uppercase hidden-xs"></span>&nbsp;
                <i class="fa fa-angle-down"></i>
              </div>
            </div>
          </div>
          <h1 class="page-title"> Admin Dashboard</h1>
          <small>statistics, charts, recent events and reports</small>
          <div class="row">
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-12">
              <a class="dashboard-stat dashboard-stat-v2 blue" href="#">
                <div class="visual">
                  <i class="fa fa-comments"></i>
                </div>
                <div class="details">
                  <div class="number">
                    <span data-counter="counterup" data-value="1349">
                      {data.User_aggregate.aggregate.count}
                    </span>
                  </div>
                  <div class="desc"> Users </div>
                </div>
              </a>
            </div>
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-12">
              <a class="dashboard-stat dashboard-stat-v2 red" href="#">
                <div class="visual">
                  <i class="fa fa-bar-chart-o"></i>
                </div>
                <div class="details">
                  <div class="number">
                    <span data-counter="counterup" data-value="12,5"></span>
                    {ProjectData.Project_aggregate.aggregate.count}
                  </div>
                  <div class="desc"> Projects </div>
                </div>
              </a>
            </div>
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-12">
              <a class="dashboard-stat dashboard-stat-v2 green" href="#">
                <div class="visual">
                  <i class="fa fa-shopping-cart"></i>
                </div>
                <div class="details">
                  <div class="number">
                    <span data-counter="counterup" data-value="549">
                      {CandidatsData.Candidate_aggregate.aggregate.count}
                    </span>
                  </div>
                  <div class="desc"> Pending Candidats </div>
                </div>
              </a>
            </div>
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-12">
              <a class="dashboard-stat dashboard-stat-v2 purple" href="#">
                <div class="visual">
                  <i class="fa fa-globe"></i>
                </div>
                <div class="details">
                  <div class="number">
                    {" "}
                    +<span
                      data-counter="counterup"
                      data-value="89"
                    ></span>%{" "}
                  </div>
                  <div class="desc"> Brand Popularity </div>
                </div>
              </a>
            </div>{" "}
          </div>{" "}
          <FullCalendar
            plugins={[
              dayGridPlugin,
              timeGridPlugin,
              interactionPlugin,
              listPlugin,
            ]}
            headerToolbar={{
              left: "prev,next today",
              center: "title",
              right: "dayGridMonth,timeGridWeek,timeGridDay,listWeek",
            }}
            initialView="dayGridMonth"
            editable={true}
            selectable={true}
            selectMirror={true}
            dayMaxEvents={true}
            eventRemove
            events={Events.Event.map(({ id, Title, start_date }) => {
              return { title: Title, date: start_date };
            })}
            dateClick={handleDateClick}
            eventColor="#378006"

            // weekends={this.state.weekendsVisible}
            // initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
            // select={this.handleDateSelect}
            // eventContent={renderEventContent} // custom render function
            // eventsSet={this.handleEvents} // called after events are initialized/added/changed/removed
            /* you can update a remote database when these fire:
                eventAdd={function(){}}
                eventChange={function(){}}
                */
          />
          <div class="clearfix"></div>
        </div>
      </div>
    </>
  );
}
