import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm } from "react-hook-form";
import Link from "next/link";
import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import { useEffect } from "react";
import { TextField } from "@material-ui/core";
import { Form } from "react-bootstrap";

const today = new Date().toISOString().substring(0, 19);

const getProjectsIds = gql`
  {
    Project {
      id
      Description
      created_at
      Title
      delivery_date
      assigned
      contact_date
      company_name
      type
      file
      income
    }
  }
`;

const GetProjectByID = gql`
  query GetProjectByID($id: Int!) {
    Project_by_pk(id: $id) {
      id
      Description
      created_at
      Title
      delivery_date
      assigned
      contact_date
      company_name
      type
      file
      income
    }
  }
`;
const GetProjectByUser = gql`
  query User($id: Int!) {
    User(where: { project_id: { _is_null: true } }) {
      id
      Name
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Project } = await hasura.request(getProjectsIds);

  return {
    paths: Project.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(GetProjectByID, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
const UpdateProject = gql`
  mutation update_Candidate_by_pk(
    $id: Int!
    $Title: String!
    $Description: String!
    $company_name: String!
    $created_at: String!
    $delivery_date: String!
    $assigned: String!
    $contact_date: String!
    $company_name: String!
    $type: String!
    $file: String!
    $income: String!
  ) {
    update_Project_by_pk(
      pk_columns: { id: $id }
      _set: {
        Title: $Title
        Description: $Description
        company_name: $company_name
        created_at: $created_at
        Title: $Title
        delivery_date: $delivery_date
        assigned: $assigned
        contact_date: $contact_date
        company_name: $company_name
        type: $type
        file: $file
        income: $income
      }
    ) {
      id
      Title
      Description
      created_at
      delivery_date
      assigned
      contact_date
      company_name
      additional_inf
      type
      file
      income
    }
  }
`;
export default function ProjectEdit({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  const { id } = query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  const { data, mutate } = useSWR(
    [GetProjectByID, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  const uploadFileToClient = (event) => {
    if (event.target.files && event.target.files[0]) {
      const i = event.target.files[0];
      setFile(i);
      setCreateObjectURL(URL.createObjectURL(i));
    }
  };
  const onSubmit = async ({ Title, Description, company_name }) => {
    try {
      const id = data.Project_by_pk.id;
      console.log(id);
      const { update_Project_by_pk } = await hasura.request(UpdateProject, {
        id,
        Title,
        Description,
        company_name,
      });

      mutate({
        ...data,
        ...update_Project_by_pk,
      });
      router.push(`/Project/List`);
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {" "}
                        <div class="form-group">
                          <label class="control-label">Client</label>
                          <input
                            defaultValue={data.Project_by_pk.company_name}
                            name="company_name"
                            id="company_name"
                            type="text"
                            placeholder="Client"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Project Title</label>
                          <input
                            defaultValue={data.Project_by_pk.Title}
                            name="Title"
                            id="Title"
                            type="text"
                            placeholder="Project Title"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">
                            Project Description
                          </label>
                          <textarea
                            defaultValue={data.Project_by_pk.Description}
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>{" "}
                        <div class="form-group">
                          <label>Income</label>
                          <div class="form-group">
                            <input
                              defaultValue={data.Project_by_pk.income}
                              name="income"
                              class="form-control"
                              id="income"
                              type="text"
                              ref={register}
                            />
                            <span class="help-block">
                              {" "}
                              123456 => â‚¬ ___.__1.234,56{" "}
                            </span>
                          </div>
                        </div>{" "}
                        <div class="form-group">
                          <label class="control-label">type</label>
                          <input
                            defaultValue={data.Project_by_pk.type}
                            name="type"
                            id="type"
                            type="text"
                            placeholder="type"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label>starting date</label> <br />
                          <TextField
                            id="datetime"
                            name="datetime"
                            type="date"
                            defaultValue={today}
                            inputRef={register}
                            required
                          />
                        </div>
                        <div class="form-group">
                          <label>expected delievry date</label> <br />
                          <TextField
                            id="datetime"
                            name="datetime"
                            type="date"
                            defaultValue={today}
                            inputRef={register}
                            required
                          />
                        </div>{" "}
                        <div>
                          {" "}
                          <Form.Group controlId="formFile" className="mb-3">
                            <Form.Control
                              defaultValue={data.Project_by_pk.file}
                              name="File"
                              type="file"
                              onChange={uploadFileToClient}
                            />
                          </Form.Group>
                        </div>
                        <br />
                        <br />
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                          <a href="#" class="btn default">
                            {" "}
                            Cancel{" "}
                          </a>
                        </div>
